mapper.select:
SELECT id, name, email, phone, creator, createdAt, updater, updatedAt
    FROM Member
    WHERE id = #{id}

SELECT id, name, email, phone, creator, createdAt, updater, updatedAt
    FROM Member
    WHERE 1 = 1
    AND name = #{name}
    AND email = #{email}
    AND phone = #{phone}
    ORDER BY createdAt DESC

mapper.update:
UPDATE Member SET
    updater = #{updater}
    , password = #{password}
    , name = #{name}
    , email = #{email}
    , phone = #{phone}
    WHERE id = #{id}

mapper.delete:
UPDATE Member SET
    used = 'N'
    WHERE id = #{id}
